import './App.css';
import React, {useState, useEffect} from "react";
import Axios from 'axios';

function App() {
  const [illnessName, setillnessName] = useState('');
  const [illnessInfo, setillnessInfo] = useState('');
  const [illnessWishList, setillnessWishList] = useState([]);
  const [newReview, setNewReview] = useState("");

  useEffect(() => {
    Axios.get('http://localhost:3002/api/get').then((response) => {
      setillnessWishList(response.data)
    })
  },[])

  const submitReview = () => { 
    Axios.post('http://localhost:3002/api/insert', {
      illness_name: illnessName,
      illness_description: illnessInfo
    });

    setillnessWishList([
      ...illnessWishList,
      {
        illness_name: illnessName,
        illness_description: illnessInfo
      },
    ]);
  };

  const deleteReview = (illnessName) => {
    Axios.delete(`http://localhost:3002/api/delete/${illnessName}`);
  };

  const updateReview = (illnessName) => {
    Axios.put(`http://localhost:3002/api/update`, {
      illnessName: illnessName,
      movieReview: newReview
    });
    setNewReview("")
  };

  return (
    <div className="App">
      <h1> CRUD APPLICATIONS</h1>

      <div className="form">
        <label> Wishlist for new illness information:</label>
        <input type="text" name="illnessName" onChange={(e) => {
          setillnessName(e.target.value)
        } }/>
        <label> Illness information:</label>
        <input type="text" name="illnessInfo" onChange={(e) => {
          setillnessInfo(e.target.value)
        }}/>

        <button onClick={submitReview}> Submit</button>

        {illnessWishList.map((val) => {
          return (
            <div className = "card">
              <h1> illness name: {val.illnessName} </h1>
              <p>illness description: {val.illnessInfo}</p>
              <button onClick={() => { deleteReview(val.illnessName) }}> Delete</button>
              <input type="text" id="updateInput" onChange={(e) => {
                setNewReview(e.target.value)
              } }/>
              <button onClick={() => {
                updateReview(val.illnessName)
              }}> Update</button>
              </div>
          );

          ;
        })}


      </div>

    </div>
  );
}

export default App;
