  //     illnessName: illnessName,
  //     movieReview: newReview
  //   });
  //   setNewReview("")
  // };

  return (
    <div className="App">
      <h1> INSERT WHISHLIST</h1>

      <div className="form">
        <label> Wishlist for new illness information:</label>
        <input type="text" name="illnessName" onChange={(e) => {
          setillnessName(e.target.value)
        } }/>
        <label> Illness information:</label>
        <input type="text" name="illnessInfo" onChange={(e) => {
          setillnessInfo(e.target.value)
        }}/>

        <button onClick={submitWish}> Submit</button>


        <h1> SEARCH</h1>

        <div className="form">
          <label> Search for a existing Illiness:</label>
          <input type="text" name="findIll" onChange={(e) => {
            setfindIll(e.target.value)
          } }/>

          <button onClick={() => { submitSearch(val.findIll) }}> Submit</button>

        {/* {illnessWishList.map((val) => {
          return (
